        static void WriteString(string text)
        {
            for (int i = 0; i < text.Length; i++)
            {
                Console.Write(text[i]);
                Thread.Sleep(60);
            }
            Console.WriteLine("");
        }

        static void ReadAndPrintFiles(string folderPath, ref character player_fnc)
        {
            string file = folderPath;
            {
                OpenLink("https://www.youtube.com/watch?v=YdSuhNDGZVg");
                Console.WriteLine($"파일: {Path.GetFileName(file)}");
                Console.WriteLine("====================================");

                try
                {
                    using (StreamReader sr = new StreamReader(file))
                    {
                        string line;
                        while ((line = sr.ReadLine()) != null)
                        {
                            WriteString(line); // 한 줄씩 출력
                            if (line.Contains("#"))
                            {
                                GetEvent(line[1].ToString(), ref player_fnc);
                            }
                        }
                    }
                }
                catch (Exception ex)
                {
                    Console.WriteLine($"파일을 읽는 중 오류 발생: {ex.Message}");
                }
            }
        }

