        //전투관련
        static int RollDice(int max_dice)
        {
            Random random = new Random(); // Random 객체 생성
            int dice = random.Next(1, max_dice + 1); // 1부터 max_dice까지의 랜덤 값 생성
            return dice;
        }
        static int Attack_Player(ref character player_char)
        {
            return RollDice(player_char.power);
        }
        static int Attack_Mob(mob mob)
        {
            return RollDice(mob.power);
        }
        static bool Avoid_Decision(ref character player_char)
        {
            bool decision = false;
            int avoid = RollDice(player_char.stamina);

            if(avoid >= 5)
            {
                decision = true;
            }
            return decision;
        }
        //플레이어 대미지 적용
        static void Calc_Damaged_Player(ref character player_char, int damage)
        {
            player_char.hp -= damage;
        }
        //몬스터 대미지 적용
        static void Calc_Damaged_Mob(ref mob mob_char, int damage)
        {
            mob_char.hp -= damage;
        }

        static void SetPlayerStat(ref character player_char, int hp, int stamina, int power)
        {
            player_char.hp = hp;
            player_char.stamina = stamina;
            player_char.power = power;
        }

        public struct character
        {
            public int hp;
            public int stamina;
            public int power;

            public character(int hp, int stamina, int power)
            {
                this.hp = hp;
                this.stamina = stamina;
                this.power = power;
            }
        }

        public struct mob
        {
            public string name;
            public int hp;
            public int power;

            public mob(string name, int hp, int power) 
            {
                this.name = name;
                this.hp = hp;
                this.power = power;
            }
        }
        static void OpenLink(string url)
        {
            try
            {
                Process.Start(new ProcessStartInfo
                {
                    FileName = url,
                    UseShellExecute = true
                });
            }
            catch (Exception ex)
            {
                Console.WriteLine($"링크를 여는 중 오류 발생: {ex.Message}");
            }
        }

        static List<string> GetSave(string save_path)
        {
            List<string> saveFiles = new List<string>();
            
            // 디렉토리가 존재하는지 확인
            if (Directory.Exists(save_path))
            {
                // "Save" 폴더 내의 모든 .txt 파일 가져오기
                string[] files = Directory.GetFiles(save_path, "save*.txt");

                // 리스트에 추가
                saveFiles.AddRange(files);
            }
            else
            {
                Console.WriteLine("Save 폴더가 존재하지 않습니다.");
            }

            return saveFiles;
        }

